import React, { useState, useEffect } from "react";
import { Form, Button, Modal } from "react-bootstrap";
import { useUserAuth } from "../context/UserAuthContext"; // Assuming you have a UserAuthContext for managing user authentication
import FBDataService from "../context/FBService"; // Assuming you have a service to handle Firestore operations

const UserProfile = () => {
  const { currentUser } = useUserAuth(); // Get the current logged-in user
  const [userProfile, setUserProfile] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        if (currentUser) {
          const userDoc = await FBService.getData(currentUser.uid); // Assuming getUserData is a method to fetch user data by UID
          setUserProfile(userDoc.data());
        }
      } catch (error) {
        console.error("Error fetching user profile:", error);
      }
    };

    fetchUserProfile();
  }, [currentUser]);

  const handleEdit = () => {
    setShowModal(true);
  };

  const handleSave = async () => {
    try {
      if (userProfile) {
        await FBDataService.updateUserData(currentUser.uid, userProfile); // Assuming updateUserData is a method to update user data
        setShowModal(false);
      }
    } catch (error) {
      console.error("Error updating user profile:", error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserProfile({ ...userProfile, [name]: value });
  };

  return (
    <div className="user-profile-container">
      <h2>User Profile</h2>
      {userProfile ? (
        <div>
          <p><strong>Name:</strong> {userProfile.name}</p>
          <p><strong>Email:</strong> {userProfile.email}</p>
          <p><strong>Mobile:</strong> {userProfile.mobile}</p>
          <p><strong>Address:</strong> {userProfile.address}</p>
          <Button variant="primary" onClick={handleEdit}>
            Edit Profile
          </Button>
        </div>
      ) : (
        <p>Loading profile...</p>
      )}

      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Edit Profile</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {userProfile && (
            <Form>
              <Form.Group controlId="formName">
                <Form.Label>Name</Form.Label>
                <Form.Control
                  type="text"
                  name="name"
                  value={userProfile.name}
                  onChange={handleChange}
                />
              </Form.Group>
              <Form.Group controlId="formMobile">
                <Form.Label>Mobile</Form.Label>
                <Form.Control
                  type="text"
                  name="mobile"
                  value={userProfile.mobile}
                  onChange={handleChange}
                />
              </Form.Group>
              <Form.Group controlId="formAddress">
                <Form.Label>Address</Form.Label>
                <Form.Control
                  type="text"
                  name="address"
                  value={userProfile.address}
                  onChange={handleChange}
                />
              </Form.Group>
            </Form>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>
            Close
          </Button>
          <Button variant="primary" onClick={handleSave}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default UserProfile;
