import { db } from "../firebase";
import {
  collection,
  getDocs,
  getDoc,
  addDoc,
  updateDoc,
  deleteDoc,
  doc,
  setDoc,
} from "firebase/firestore";

const collectionName = "users";
const collectionRef = collection(db, collectionName);

class FBDataService {
  
  // Adds a new document to the collection
  addData = (newData) => {
    return addDoc(collectionRef, newData);
  }

  // Sets or updates a document with a specific ID (overwrites data)
  setData = (newData) => {
    const docRef = doc(db, collectionName, newData.id);
    return setDoc(docRef, newData, { merge: true });
  }

  // Updates specific fields in an existing document
  updateData = (id, newData) => {
    const docRef = doc(db, collectionName, id);
    return updateDoc(docRef, newData);
  }

  // Deletes a document by ID
  deleteData = (id) => {
    const docRef = doc(db, collectionName, id);
    return deleteDoc(docRef);
  }

  // Gets a specific document by ID
  getData = (id) => {
    const docRef = doc(db, collectionName, id);
    return getDoc(docRef);
  }

  // Retrieves all documents in the collection
  getAllData = () => {
    return getDocs(collectionRef);
  }
}

export default new FBDataService();
