import React, { useEffect, useState } from "react";
import { useUserAuth } from "../context/UserAuthContext";
import FBDataService from "../services/FBDataService";
import { Button, Form, Modal } from "react-bootstrap";

const UserProfile = () => {
  const { currentUser } = useUserAuth();
  const [userProfile, setUserProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [updatedProfile, setUpdatedProfile] = useState({});

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        if (currentUser) {
          console.log("Fetching data for UID:", currentUser.uid);
          const userDoc = await FBDataService.getData(currentUser.uid);
          if (userDoc.exists()) {
            console.log("User data found:", userDoc.data());
            setUserProfile(userDoc.data());
            setLoading(false);
          } else {
            console.log("No such user!");
            setLoading(false);
          }
        }
      } catch (error) {
        console.error("Error fetching user profile:", error);
        setLoading(false);
      }
    };

    fetchUserProfile();
  }, [currentUser]);

  const handleEdit = () => {
    setUpdatedProfile(userProfile);
    setShowModal(true);
  };

  const handleSave = async () => {
    try {
      await FBDataService.updateData(currentUser.uid, updatedProfile);
      setUserProfile(updatedProfile);
      setShowModal(false);
    } catch (error) {
      console.error("Error updating profile:", error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUpdatedProfile({ ...updatedProfile, [name]: value });
  };

  if (loading) {
    return <div>Loading profile...</div>;
  }

  return (
    <div>
      {userProfile ? (
        <div>
          <h2>User Profile</h2>
          <p>Name: {userProfile.name}</p>
          <p>Email: {userProfile.email}</p>
          {/* Add more fields as necessary */}
          <Button variant="primary" onClick={handleEdit}>
            Edit Profile
          </Button>

          <Modal show={showModal} onHide={() => setShowModal(false)}>
            <Modal.Header closeButton>
              <Modal.Title>Edit Profile</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <Form>
                <Form.Group controlId="formUserName">
                  <Form.Label>Name</Form.Label>
                  <Form.Control
                    type="text"
                    name="name"
                    value={updatedProfile.name}
                    onChange={handleChange}
                  />
                </Form.Group>
                <Form.Group controlId="formUserEmail">
                  <Form.Label>Email</Form.Label>
                  <Form.Control
                    type="email"
                    name="email"
                    value={updatedProfile.email}
                    onChange={handleChange}
                  />
                </Form.Group>
                {/* Add more fields as necessary */}
              </Form>
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={() => setShowModal(false)}>
                Close
              </Button>
              <Button variant="primary" onClick={handleSave}>
                Save Changes
              </Button>
            </Modal.Footer>
          </Modal>
        </div>
      ) : (
        <div>No user profile found.</div>
      )}
    </div>
  );
};

export default UserProfile;
