import React, { useEffect, useState } from "react";
import { useUserAuth } from "../context/UserAuthContext";
import FBDataService from "../context/FBService";
import { Button, Form, Modal, Alert } from "react-bootstrap";

const UserProfile = () => {
  const { currentUser } = useUserAuth(); // Get the current user from AuthContext
  const [userProfile, setUserProfile] = useState(null); // Store the user profile data
  const [loading, setLoading] = useState(true); // Loading state for fetching data
  const [showModal, setShowModal] = useState(false); // For editing profile
  const [updatedProfile, setUpdatedProfile] = useState({}); // Store updated profile data
  const [error, setError] = useState(""); // Store any errors

  useEffect(() => {
    const fetchUserProfile = async () => {
      if (!currentUser) {
        setError("No current user logged in");
        setLoading(false);
        return;
      }
      try {
        console.log("Fetching user profile for UID:", currentUser.uid);
        const userDoc = await FBDataService.getData(currentUser.uid);
        if (userDoc.exists()) {
          console.log("User data found:", userDoc.data());
          setUserProfile(userDoc.data());
        } else {
          console.log("No such user!");
          setError("User not found in Firestore");
        }
      } catch (err) {
        console.error("Error fetching user profile:", err);
        setError("Error fetching user data");
      } finally {
        setLoading(false);
      }
    };

    fetchUserProfile();
  }, [currentUser]);

  const handleEdit = () => {
    setUpdatedProfile(userProfile);
    setShowModal(true);
  };

  const handleSave = async () => {
    try {
      await FBDataService.updateData(currentUser.uid, updatedProfile);
      setUserProfile(updatedProfile);
      setShowModal(false);
    } catch (error) {
      console.error("Error updating profile:", error);
      setError("Error updating profile");
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUpdatedProfile({ ...updatedProfile, [name]: value });
  };

  if (loading) {
    return <div>Loading profile...</div>;
  }

  return (
    <div>
      {error && <Alert variant="danger">{error}</Alert>}
      {userProfile ? (
        <div>
          <h2>User Profile</h2>
          <p>Name: {userProfile.name}</p>
          <p>Email: {userProfile.email}</p>
          <p>Mobile: {userProfile.mobile}</p>
          <p>Username: {userProfile.username}</p>
          <p>Age: {userProfile.age}</p>
          <Button variant="primary" onClick={handleEdit}>
            Edit Profile
          </Button>

          <Modal show={showModal} onHide={() => setShowModal(false)}>
            <Modal.Header closeButton>
              <Modal.Title>Edit Profile</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <Form>
                <Form.Group controlId="formUserName">
                  <Form.Label>Name</Form.Label>
                  <Form.Control
                    type="text"
                    name="name"
                    value={updatedProfile.name || ""}
                    onChange={handleChange}
                  />
                </Form.Group>
                <Form.Group controlId="formUserEmail">
                  <Form.Label>Email</Form.Label>
                  <Form.Control
                    type="email"
                    name="email"
                    value={updatedProfile.email || ""}
                    onChange={handleChange}
                  />
                </Form.Group>
                {/* Add more fields as necessary */}
              </Form>
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={() => setShowModal(false)}>
                Close
              </Button>
              <Button variant="primary" onClick={handleSave}>
                Save Changes
              </Button>
            </Modal.Footer>
          </Modal>
        </div>
      ) : (
        <div>User not found.</div>
      )}
    </div>
  );
};

export default UserProfile;
